package simulation._symboltable;

import de.monticore.symboltable.MutableScope;
import de.monticore.symboltable.ResolvingConfiguration;

import de.monticore.symboltable.ArtifactScope;
import de.monticore.symboltable.Scope;
import de.se_rwth.commons.logging.Log;

public class SimulationModelLoader extends de.monticore.modelloader.ModelingLanguageModelLoader<simulation._ast.ASTSimulation> {

  public SimulationModelLoader(SimulationLanguage language) {
    super(language);
  }

  @Override
  protected void createSymbolTableFromAST(final simulation._ast.ASTSimulation ast, final String modelName,
    final MutableScope enclosingScope, final ResolvingConfiguration resolvingConfiguration) {
    final SimulationSymbolTableCreator symbolTableCreator =
            getModelingLanguage().getSymbolTableCreator(resolvingConfiguration, enclosingScope).orElse(null);

    if (symbolTableCreator != null) {
      Log.debug("Start creation of symbol table for model \"" + modelName + "\".",
          SimulationModelLoader.class.getSimpleName());
      final Scope scope = symbolTableCreator.createFromAST(ast);

      if (!(scope instanceof ArtifactScope)) {
        Log.warn("0xA7001_298 Top scope of model " + modelName + " is expected to be an artifact scope, but"
          + " is scope \"" + scope.getName() + "\"");
      }

      Log.debug("Created symbol table for model \"" + modelName + "\".", SimulationModelLoader.class.getSimpleName());
    }
    else {
      Log.warn("0xA7002_298 No symbol created, because '" + getModelingLanguage().getName()
        + "' does not define a symbol table creator.");
    }
  }

  @Override
  public SimulationLanguage getModelingLanguage() {
    return (SimulationLanguage) super.getModelingLanguage();
  }
}
